import React, {useState, useEffect} from 'react';
import Post from './Post';
import Logo from "../images/logo.svg";
import '../css/App.css';
import { db, auth } from './Firebase';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import { Button, Input } from '@material-ui/core';
import UploadImage from './UploadImage'


function getModalStyle() {
    const top = 50
    const left = 50

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme) => ({
    paper: {
        position: 'absolute',
        width: 400,
        backgroundColor: theme.palette.background.paper,
        border: '2px solid #000',
        boxShadow: theme.shadows[5],
        padding: theme.spacing(2, 4, 3),
    },
}));

function App() {

    const classes = useStyles();
    const [modalStyle] = useState(getModalStyle);
    const [posts, setPosts] = useState([]);
    const [open, setOpen] = useState(false); // Default value is false

    // User Registration and Authentication using Firebase
    const [username, setUsername] = useState(''); 
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const [user, setUser] = useState(null);

    const [openLogIn, setOpenLogIn] = useState(false);

    useEffect(()=>{ // treat this as the frontend listener
        // treat everything below as the backend listener
        const unsubscribe = auth.onAuthStateChanged((authUser) => { 
        // auth.OnAuthStateChanged is a listerner and each time a change is made, it will run the folowung code.
            if (authUser) {
                // user is logged in.
                setUser(authUser); // it will not be affected when the website refreshes because of cookies and of auth.onAuthStateChanged
            } else {
                // user is logged out, set it back to null
                setUser(null); 
            }
        }) 

        return () => {
        // if the useEffect is run again, performs some cleanup before the run is re-run.
            unsubscribe();
        }

    }, [user, username]);


    useEffect(()=> {
        db.collection('posts').orderBy('timestamp', 'desc').onSnapshot(snapshot => {
            setPosts(snapshot.docs.map(doc => ({
                id: doc.id,
                post: doc.data()
            }))); 
        })

    }, []);

    const register = (event) => {
        event.preventDefault();

        auth
        .createUserWithEmailAndPassword(email,password)
        .then((authUser) => {
            return authUser.user.updateProfile({
                displayName: username
            })
        })
        .catch((error) => alert(error.message))

        setOpen(false);
    };

    const logIn = (event) => {
        event.preventDefault();

        auth
        .signInWithEmailAndPassword(email, password)
        .then ((authUser) => {})
        .catch((error) => alert(error.message))

        setOpenLogIn(false);
        setEmail("");
        setPassword("");
    };

    {/* 
        [] is variable and if we leave it blank, this means that the code will run once
        when the App component loads, and will not run again.

        [posts] means that the App component will run one time and run again when the details of posts changes
    */}

    return (
        <div className="app">

        <Modal
            open={openLogIn}
            onClose={() => setOpenLogIn(false)}
        >

            <div style={modalStyle} className={classes.paper}>
                <center>
                    <div className="app__login-register-welcome-text">
                        Welcome back to SHARE IT! <br></br>
                        It's nice to see you again ðŸ˜„
                    </div>
                    <form className="app__login-register">
                        <Input
                            placeholder="email"
                            type="text"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />

                        <Input
                            placeholder="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <br></br>

                        <Button type="submit" onClick={logIn}>Submit</Button>
                    </form>
                </center>
            </div>

        </Modal>

        <Modal
            open={open}
            onClose={() => setOpen(false)}
        >

            <div style={modalStyle} className={classes.paper}>
                <center>
                    <div className="app__login-register-welcome-text">
                        Welcome to SHARE IT âœ¨ <br></br>
                        Please complete the form below to become a member.
                    </div>
                    <form className="app__login-register">
                        <Input
                            placeholder="username"
                            type="text"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />

                        <Input
                            placeholder="email"
                            type="text"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />

                        <Input
                            placeholder="password"
                            type="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                        <br></br>

                        <Button type="submit" onClick={register}>Submit</Button>
                    </form>
                </center>
            </div>

        </Modal>

        {/* Header */}
        <div className="app__header"><a href=""> <img className="app__header-image" src={Logo} alt="" /></a>
            <div className="nav">
                { user ? (
                    <Button 
                        style={{
                            fontSize: "15px",
                            color: "#edeae5"
                        }}
                        onClick={() => auth.signOut()}
                    >
                        Log Out
                    </Button>
                ) : (
                    <div>
                        <Button 
                            style={{
                                fontSize: "15px",
                                color: "#edeae5"
                            }}
                            onClick={() => setOpenLogIn(true)}
                        >
                            Log In
                        </Button>

                        <Button 
                            style={{
                                fontSize: "15px",
                                color: "#edeae5"
                            }}
                            onClick={() => setOpen(true)}
                        >
                            Register
                        </Button>
                    </div>
                )}
            </div>
        </div>

        <div className="app__upload">
            {user?.displayName ? ( // the ? is an optional, basically a try catch statement
                <UploadImage username={user.displayName}/>
            ) : (
                <div>Login to comment or post a photo!</div>
            )}
        </div>

        {
            posts.map(({id, post}) => (
                <Post key={id} postId={id} user={user} username={post.username} caption={post.caption} imageUrl={post.imageUrl}/>
            ))
        }
        </div>
    );
}

export default App;
